import React, { useState } from 'react';
import './Login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            if (response.ok) {
                window.location.href = '/home';
            } else {
                const data = await response.json();
                setError(data.message);
            }
        } catch (error) {
            console.error('Error logging in', error);
            setError('Server error');
        }
    };

    return (
        <section className="vh-100 gradient-custom">
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                        <div className="card bg-dark text-white" style={{ borderRadius: "1rem" }}>
                            <div className="card-body p-5 text-center">
                                <div className="mb-md-0 mt-md-0 pb-1">
                                    <h2 className="fw-bold mb-2 text-uppercase">AVE.DIAMONDS</h2>
                                    <p className="fs-5 text-white-60 mb-3">Авторизация</p>

                                    <form onSubmit={handleSubmit}>
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="staticEmail" className="fs-5 col-4 col-form-label">Почта</label>
                                            <div className="col-8">
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    id="staticEmail"
                                                    placeholder="email@example.com"
                                                    value={email}
                                                    onChange={(e) => {setEmail(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="inputPassword" className="fs-5 col-4 col-form-label">Пароль</label>
                                            <div className="col-8">
                                                <input
                                                    type="password"
                                                    className="form-control"
                                                    id="inputPassword"
                                                    placeholder="********"
                                                    value={password}
                                                    onChange={(e) => {
                                                        setPassword(e.target.value);
                                                    }}
                                                />
                                            </div>
                                        </div>

                                        <p className="small mb-3 pb-lg-2"><a className="text-white-50" href="#!">Не помню пароль</a></p>

                                        <button
                                            className="btn btn-outline-light btn-lg px-5"
                                            type="submit"
                                        >
                                            Войти
                                        </button>
                                    </form>

                                    {error && <p className="text-danger mt-3">{error}</p>}

                                </div>

                                <div>
                                    <p className="mb-0">
                                        <a href="#!" className="text-white-50 fw-bold">Зарегистрироваться</a>
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Login;
