import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductCarousel() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    return (
        <section className="vh-100" id={multiCarousel}>
                {products.map(product => (
                    <Carousel.Item key={product.id}>
                        <img className="d-block w-100" src={product.image_url} alt={product.name} />
                        <Carousel.Caption>
                            <h3 className="card-title">{product.name}</h3>
                            <p className="card-text">{product.stone_name} {product.carat}</p>
                            <p className="card-text">{product.metal_name} {product.purity}</p>
                        </Carousel.Caption>
                    </Carousel.Item>
                ))}
        </section>
    );
}

export default ProductCarousel;
