import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import Carousel from "./Carousel";
import { addToCart } from './js/addToCart.js';
import { exportCart } from './js/exportCart.js';
import minus from './img/minus.svg';
import plus from './img/plus.svg';

const ProductCard = () => {
    const navigate = useNavigate();
    const params = useParams();
    const productId = params.productId;
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [count, setCount] = useState(0); // Добавляем состояние для отслеживания количества товаров

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`/ware/${productId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch product');
                }
                const productData = await response.json();
                setProduct(productData);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProduct();
    }, [productId]);

    const handleQuantityChange = async (action) => {
        try {
            // Обновляем состояние count в зависимости от действия (увеличение или уменьшение)
            if (action === 'increase') {
                setCount(count + 1);
            } else if (action === 'decrease' && count > 0) {
                setCount(count - 1);
            }
        } catch (error) {
            console.error('Error updating cart items:', error);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!product) {
        return <div>Product not found</div>;
    }

    return (
        <div>
            <Header />
            <div className="container py-5">
                <div className="row">
                    <div className="col-md-6">
                        <img src={product.image_url} alt={product.name} className="img-fluid" />
                    </div>
                    <div className="col-md-6">
                        <h1>{product.name}</h1>
                        <p>Камень: {product.stone_name} {product.carat} карат</p>
                        <p>Металл: {product.metal_name} {product.purity} пробы</p>
                        <p>Поставщик: {product.provider_name}</p>
                        <h3>{product.price} ₽</h3>
                        {count > 0 ? (
                            <div className="d-flex align-items-center">
                                <img src={minus} width="20" height="20" alt="Minus" className="me-2" onClick={() => handleQuantityChange('decrease')} />
                                <input min="0" name="quantity" value={count} type="number" className="form-control" readOnly />
                                <img src={plus} width="20" height="20" alt="Plus" className="ms-2" onClick={() => handleQuantityChange('increase')} />
                            </div>
                        ) : (
                            <button className="btn btn-primary" onClick={() => addToCart(product.id, navigate)}>Добавить в корзину</button>
                        )}
                    </div>
                </div>
            </div>
            <Carousel />
            <Footer />
        </div>
    );
};

export default ProductCard;
