import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';

function Register() {
    const [step, setStep] = useState(1);
    const [phoneNumber, setPhoneNumber] = useState('');
    const [fullName, setFullName] = useState({ surname: '', name: '', secondName: '' });
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    const handleNext = () => {
        setError('');
        setStep(step + 1);
    };

    const handlePrevious = () => {
        setError('');
        setStep(step - 1);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Добавьте здесь логику для отправки данных на сервер
    };

    return (
        <section className="vh-100 gradient-custom">
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                        <div className="card bg-dark text-white" style={{ borderRadius: "1rem" }}>
                            <div className="card-body p-5 text-center">
                                {step === 1 && (
                                    <div>
                                        <div className="position-relative">
                                            <Link to="/login" className="btn btn-link position-absolute top-0 start-0 m-2">
                                                <img src="img/back.png" alt="Back" width="320" height="512" />
                                            </Link>
                                        </div>
                                        <h2 className="fw-bold mb-2 text-uppercase">Регистрация</h2>
                                        <p className="fs-5 text-white-60 mb-3">Введите номер телефона</p>
                                        <form onSubmit={handleNext}>
                                            <div className="mb-3 row align-items-center">
                                                <label htmlFor="phoneNumber" className="fs-5 col-4 col-form-label">Номер телефона</label>
                                                <div className="col-8">
                                                    <input
                                                        type="tel"
                                                        className="form-control"
                                                        id="phoneNumber"
                                                        placeholder="Введите номер телефона"
                                                        value={phoneNumber}
                                                        onChange={(e) => setPhoneNumber(e.target.value)}
                                                    />
                                                </div>
                                            </div>
                                            <button className="btn btn-outline-light btn-lg px-5" type="submit">Далее</button>
                                        </form>
                                    </div>
                                )}

                                {step === 2 && (
                                    <div>
                                        <div className="position-relative">
                                            <button className="btn btn-link position-absolute top-0 start-0 m-2" onClick={handlePrevious}>
                                                <img src="img/back.png" alt="Back" width="20" height="20" />
                                            </button>
                                        </div>
                                        <h2 className="fw-bold mb-2 text-uppercase">Регистрация</h2>
                                        <p className="fs-5 text-white-60 mb-3">Введите ФИО и адрес электронной почты</p>
                                        <form onSubmit={handleNext}>
                                            <div className="mb-3 row align-items-center">
                                                <label htmlFor="surname" className="fs-5 col-4 col-form-label">Фамилия</label>
                                                <div className="col-8">
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        id="surname"
                                                        placeholder="Введите фамилию"
                                                        value={fullName.surname}
                                                        onChange={(e) => setFullName({ ...fullName, surname: e.target.value })}
                                                    />
                                                </div>
                                            </div>
                                            <div className="mb-3 row align-items-center">
                                                <label htmlFor="name" className="fs-5 col-4 col-form-label">Имя</label>
                                                <div className="col-8">
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        id="name"
                                                        placeholder="Введите имя"
                                                        value={fullName.name}
                                                        onChange={(e) => setFullName({ ...fullName, name: e.target.value })}
                                                    />
                                                </div>
                                            </div>
                                            <div className="mb-3 row align-items-center">
                                                <label htmlFor="secondname" className="fs-5 col-4 col-form-label">Отчество</label>
                                                <div className="col-8">
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        id="secondname"
                                                        placeholder="Введите отчество"
                                                        value={fullName.secondname}
                                                        onChange={(e) => setFullName({ ...fullName, secondname: e.target.value })}
                                                    />
                                                </div>
                                            </div>
                                            <button className="btn btn-outline-light btn-lg px-5" type="submit">Далее</button>
                                        </form>
                                    </div>
                                )}

                                {step === 3 && (
                                    <div>
                                        <div className="position-relative">
                                            <button className="btn btn-link position-absolute top-0 start-0 m-2" onClick={handlePrevious}>
                                                <img src="img/back.png" alt="Back" width="20" height="20" />
                                            </button>
                                        </div>
                                        <h2 className="fw-bold mb-2 text-uppercase">Регистрация</h2>
                                        <p className="fs-5 text-white-60 mb-3">Введите пароль</p>
                                        <form onSubmit={handleSubmit}>
                                            <div className="mb-3 row align-items-center">
                                                <label htmlFor="password" className="fs-5 col-4 col-form-label">Пароль</label>
                                                <div className="col-8">
                                                    <input
                                                        type="password"
                                                        className="form-control"
                                                        id="password"
                                                        placeholder="Введите пароль"
                                                        value={password}
                                                        onChange={(e) => setPassword(e.target.value)}
                                                    />
                                                </div>
                                            </div>
                                            <button className="btn btn-outline-light btn-lg px-5" type="submit">Зарегистрироваться</button>
                                        </form>
                                    </div>
                                )}
                                {error && <p className="text-danger mt-3">{error}</p>}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Register;
