import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Header() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        navigate('/login');
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-body-tertiary">
            <div className="container d-flex justify-content-between align-items-center">
                <div className="d-flex align-items-center">
                    <Link className="nav-link me-3" to="/catalog">Каталог</Link>
                    {!isAuthenticated && (
                        <Link to="/login" className="btn btn-primary me-3">Вход</Link>
                    )}
                    {isAuthenticated && (
                        <>
                            <Link to="/cart" className="btn btn-primary me-3">
                                <img src="/path-to-your-cart-icon.png" alt="Корзина" width="20" height="20" />
                            </Link>
                            <button className="btn btn-primary" onClick={handleLogout}>Выйти</button>
                        </>
                    )}
                </div>
                <div className="text-center">
                    <Link className="navbar-brand" to="/">AVE.DIAMONDS</Link>
                    <ul className="navbar-nav justify-content-center mt-2">
                        <li className="nav-item">
                            <Link className="nav-link" to="/about">О нас</Link>
                        </li>
                    </ul>
                </div>
            </div>
        </nav>
    );
}

export default Header;
