import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';

const ProductCard = () => {
    const { productId } = useParams().productId;
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                alert(productId)
                const response = await fetch(`/ware/${productId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch product');
                }
                const productData = await response.json();
                setProduct(productData);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProduct();
    }, [productId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    if (!product) {
        return <div>Product not found</div>;
    }

    return (
        <h2>Товар id: {prodId}   категория: {prodCat}</h2>
        // <div>
        //     <Header />
        //     <div className="container py-5">
        //         <div className="row">
        //             <div className="col-md-6">
        //                 <img src={product.image_url} alt={product.name} className="img-fluid" />
        //             </div>
        //             <div className="col-md-6">
        //                 <h1>{product.name}</h1>
        //                 <p>{product.description}</p>
        //                 <h3>{product.price} ₽</h3>
        //                 <button className="btn btn-primary">Добавить в корзину</button>
        //             </div>
        //         </div>
        //     </div>
        //     <Footer />
        // </div>
    );
};

export default ProductCard;
