import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import { addToCart } from './js/addToCart';
import { quantityChange } from './js/quantityChange';
import minus from './img/minus.svg';
import plus from './img/plus.svg';
import './All.css';
import favorite from "./img/favorite.svg";

function Favorite() {
    const navigate = useNavigate();
    const [favoriteItems, setFavoriteItems] = useState([]);
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        const fetchFavoriteItems = async () => {
            try {
                const response = await fetch('/favorite', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`
                    }
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch favorite items');
                }
                const data = await response.json();
                setFavoriteItems(data);
            } catch (error) {
                console.error('Error fetching favorite items:', error);
            }
        };

        fetchFavoriteItems();
    }, []);

    useEffect(() => {
        const fetchCart = async () => {
            try {
                // Fetch cart items
            } catch (error) {
                console.error('Error fetching cart items:', error);
            }
        };

        fetchCart();
    }, []);

    const handleQuantityChange = async (productId, action) => {
        // Handle quantity change
    };

    const removeFromFavorites = async (productId) => {
        try {
            const response = await fetch(`/favorite/remove/${productId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            if (!response.ok) {
                throw new Error('Failed to remove item from favorites');
            }
            // Обновляем список избранных товаров
            const updatedFavoriteItems = favoriteItems.filter(item => item.id !== productId);
            setFavoriteItems(updatedFavoriteItems);
        } catch (error) {
            console.error('Error removing item from favorites:', error);
        }
    };

    return (
        <section className="vh-100">
            <div className="container">
                <Header />
                <h1 className="mb-4">Избранное</h1>
                <div className="row row-cols-1 row-cols-md-3 g-4">
                    {favoriteItems.map((product, index) => (
                        <div key={index} className="col-md-2">
                            <div className="card h-100">
                                <div className="card-body">
                                    <h5 className="card-title">{product.name}</h5>
                                    <p className="card-text">{product.jewerly_type_name}</p>
                                    <p className="card-text">{product.stone_name} {product.carat}</p>
                                    <p className="card-text">{product.metal_name}</p>
                                    <div className="d-flex justify-content-between">
                                        <button className="btn btn-primary" onClick={() => {
                                            addToCart(product.id, navigate);
                                            const updatedFavoriteItems = favoriteItems.filter(item => item.id !== product.id);
                                            setFavoriteItems(updatedFavoriteItems);
                                        }}>В корзину</button>
                                        <button className="btn btn-danger" onClick={() => removeFromFavorites(product.id)}>Удалить из избранного</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Favorite;
