import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cart from './img/cart.svg';
import logout from './img/logout.svg';
import edit from './img/edit.svg';

function Header() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        setIsAuthenticated(!!token);
        fetchUserInfo();
    }, []);

    const fetchUserInfo = async () => {
        try {
            const response = await fetch('/check-admin', {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });

            if (response.ok) {
                const userData = await response.json();
                alert(userData.admin)
                setIsAdmin(userData.admin);
            } else {
                // Обработка ошибки
                console.error('Failed to fetch user info');
            }
        } catch (error) {
            console.error('Error fetching user info:', error);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        navigate('/login');
    };

    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-body-tertiary">
            <div className="container d-flex justify-content-between align-items-center">
                <div className="d-flex align-items-center">
                    <Link className="nav-link me-3" to="/catalog">Каталог</Link>
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                        <li className="nav-item">
                            <Link className="nav-link" to="/about">О нас</Link>
                        </li>
                    </ul>

                </div>
                <Link className="navbar-brand mx-auto" to="/">AVE.DIAMONDS</Link>
                {!isAuthenticated && (
                    <Link to="/login" className="btn btn-primary me-3">Вход</Link>
                )}
                {isAuthenticated && (
                    <>
                        <Link to="/cart" className="btn btn-primary me-3">
                            <img src={cart} alt="Корзина" width="20" height="20" />
                        </Link>
                        {isAdmin && (
                            <Link to="/addWare" className="btn btn-primary me-3">
                                <img src={edit} alt="Изменить" width="20" height="20" />
                            </Link>
                        )}
                        <div className="d-flex align-items-center">
                            <button className="btn btn-primary" onClick={handleLogout}>
                                <img src={logout} alt="Logout" width="20" height="20" />
                            </button>
                        </div>
                    </>
                )}
            </div>
        </nav>
    );
}

export default Header;
