import React, { useState, useEffect } from 'react';
import Header from './Header';

function Cart() {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/ware', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch cart items');
                }
                const cartData = await response.json();
                const cartProducts = await Promise.all(cartData.jewerly_id.map(async (productId) => {
                    const productResponse = await fetch(`/ware/${productId}`);
                    if (!productResponse.ok) {
                        throw new Error(`Failed to fetch product with id ${productId}`);
                    }
                    return productResponse.json();
                }));
                setCartItems(cartProducts);
            } catch (error) {
                console.error('Error fetching cart items:', error);
            }
        };

        fetchCartItems();
    }, []);

    return (
        <section className="h-100 gradient-custom">
            <div className="container py-5">
                <div className="row d-flex justify-content-center my-4">
                    <div className="col-md-8">
                        <div className="card mb-4">
                            <div className="card-header py-3">
                                <h5 className="mb-0">Cart - {cartItems.length} items</h5>
                            </div>
                            <div className="card-body">
                                {cartItems.map((item, index) => (
                                    <CartItem key={index} item={item} />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

const CartItem = ({ item }) => {
    return (
        <div className="row">
            <div className="col-lg-3 col-md-12 mb-4 mb-lg-0">
                {/* Image */}
                <div className="bg-image hover-overlay hover-zoom ripple rounded" data-mdb-ripple-color="light">
                    <img src={item.image_url} className="w-100" alt="Product" />
                    <a href="#!">
                        <div className="mask" style={{ backgroundColor: "rgba(251, 251, 251, 0.2)" }}></div>
                    </a>
                </div>
                {/* Image */}
            </div>

            <div className="col-lg-5 col-md-6 mb-4 mb-lg-0">
                {/* Data */}
                <p><strong>{item.name}</strong></p>
                <p>Color: {item.stone_name}</p>
                {/* Add more product details here */}
            </div>

            <div className="col-lg-4 col-md-6 mb-4 mb-lg-0">
                {/* Quantity */}
                <div className="d-flex mb-4" style={{ maxWidth: "300px" }}>
                    <button className="btn btn-primary px-3 me-2">
                        <i className="fas fa-minus"></i>
                    </button>

                    <div className="form-outline">
                        <input id={`quantity-${item.id}`} min="0" name="quantity" value="1" type="number" className="form-control" />
                        <label className="form-label" htmlFor={`quantity-${item.id}`}>Количество</label>
                    </div>

                    <button className="btn btn-primary px-3 ms-2">
                        <i className="fas fa-plus"></i>
                    </button>
                </div>
                {/* Quantity */}

                {/* Price */}
                <p className="text-start text-md-center">
                    <strong>${item.price}</strong>
                </p>
                {/* Price */}
            </div>
        </div>
    );
};

export default Cart;
