import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import left from "./img/back.svg"
import right from "./img/right.svg"

function ProductCarousel() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const productsData = await response.json();
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        }

        fetchProducts();
    }, []);

    const groupedProducts = products.reduce((accumulator, currentValue, index) => {
        const groupIndex = Math.floor(index / 5);
        if (!accumulator[groupIndex]) {
            accumulator[groupIndex] = [];
        }
        accumulator[groupIndex].push(currentValue);
        return accumulator;
    }, []);

    return (
        <section className="vh-100">
            <div id="multiCarousel" className="carousel slide w-100" data-ride="carousel">
                <a className="carousel-control-prev" href="#multiCarousel" role="button" data-slide="prev">
                    <img src={left} alt="Previous" width={"30px"}/>
                </a>
                <div className="carousel-inner" role="listbox">
                    {groupedProducts.map((group, groupIndex) => (
                        <div className={`carousel-item ${groupIndex === 0 ? 'active' : ''}`} key={groupIndex}>
                            <div className="container">
                                <div className="row">
                                    {group.map(product => (
                                        <div className="col-12 col-md-6 col-lg-4 col-xl-3 col-custom rounded-5" key={product.id}>
                                            <img className="d-block w-100 rounded-5" src={product.image_url} alt={product.name} />
                                            <h5>{product.name}</h5>
                                            <p>{product.stone_name} {product.carat}</p>
                                            <p>{product.metal_name} {product.purity}</p>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
                <a className="carousel-control-next" href="#multiCarousel" role="button" data-slide="next">
                    <img src={right} alt="Next" width={"30px"}/>
                </a>
            </div>
        </section>
    );
}

export default ProductCarousel;
