import React, { useState, useEffect } from 'react';
import Header from "./Header";

function Catalog() {
    const [products, setProducts] = useState([]);
    const [sortByPrice, setSortByPrice] = useState('asc'); // Состояние для сортировки по цене (asc - по возрастанию, desc - по убыванию)

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    // Функция для сортировки товаров по цене
    const sortProductsByPrice = (products, sortByPrice) => {
        const sortedProducts = [...products];
        sortedProducts.sort((a, b) => {
            if (sortByPrice === 'asc') {
                return a.price - b.price;
            } else {
                return b.price - a.price;
            }
        });
        return sortedProducts;
    };

    const handleSortByPrice = () => {
        setSortByPrice(sortByPrice === 'asc' ? 'desc' : 'asc');
    };

    return (
        <div className="container">
            <Header/>
            <h1 className="mb-4">Catalog</h1>
            <div className="row">
                <div className="col">
                    <button className="btn btn-primary mb-3" onClick={handleSortByPrice}>
                        Sort by Price {sortByPrice === 'asc' ? 'Ascending' : 'Descending'}
                    </button>
                </div>
            </div>
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {sortProductsByPrice(products, sortByPrice).map((product, index) => (
                    <div key={index} className="col">
                        <div className="card h-100">
                            <img src="..." className="card-img-top" alt="Product Image" />
                            <div className="card-body">
                                <h5 className="card-title">{product.name}</h5>
                                <p className="card-text">{product.jewerly_type_name}</p>
                                <p className="card-text">{product.stone_name} {product.carat}</p>
                                <p className="card-text">{product.metal_name} {product.purity}</p>
                                <p className="card-text">Price: {product.price * (product.percent + 100)}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Catalog;
