import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import left from "./img/back.svg"
import right from "./img/right.svg"

function ProductCarousel() {
    const [products, setProducts] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const productsData = await response.json();
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        }

        fetchProducts();
    }, []);

    const moveNext = () => {
        setCurrentIndex(prevIndex => (prevIndex === products.length - 1 ? 0 : prevIndex + 1));
    };

    const movePrev = () => {
        setCurrentIndex(prevIndex => (prevIndex === 0 ? products.length - 1 : prevIndex - 1));
    };

    return (
        <section className="vh-100">
            <div className="carousel-inner" role="listbox">
                <div className="container">
                    <div className="row">
                        <button className="col-1" onClick={movePrev}>
                            <img src={left} alt="Previous" width={"30px"} />
                        </button>
                        {products.map((product, index) => (
                            <div
                                key={product.id}
                                className={`col-3 col-md-6 col-lg-4 col-xl-3 col-custom rounded-5 ${index === currentIndex ? 'active' : ''}`}
                            >
                                <img className="d-block w-100 rounded-5" src={product.image_url} alt={product.name} />
                                <h5>{product.name}</h5>
                                <p>{product.stone_name} {product.carat}</p>
                                <p>{product.metal_name} {product.purity}</p>
                            </div>
                        ))}
                        <button className="col-1" onClick={moveNext}>
                            <img src={right} alt="Next" width={"30px"} />
                        </button>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default ProductCarousel;
