import React, { useState } from 'react';
import './Login.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Email:', email);
        console.log('Password:', password);
    };

    return (
        <section className="vh-100 gradient-custom">
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                        <div className="card bg-dark text-white" style={{ borderRadius: "1rem" }}>
                            <div className="card-body p-5 text-center">
                                <div className="mb-md-0 mt-md-0 pb-1">
                                    <h2 className="fw-bold mb-2 text-uppercase">AVE.DIAMONDS</h2>
                                    <p className="fs-4 text-white-60 mb-3">Авторизация</p>

                                    <div className="mb-3 row">
                                        <label htmlFor="staticEmail" className="col-sm-2 col-form-label">Почта</label>
                                        <div className="col-sm-10">
                                            <input type="email" className="form-control" id="staticEmail" placeholder="email@example.com"/>
                                        </div>
                                    </div>
                                    <div className="mb-3 row">
                                        <label htmlFor="inputPassword" className="col-sm-2 col-form-label">Пароль</label>
                                        <div className="col-sm-10">
                                            <input type="password" className="form-control" id="inputPassword" placeholder="********"/>
                                        </div>
                                    </div>

                                    <p className="small mb-2 pb-lg-2"><a className="text-white-50" href="#!">Не помню пароль</a></p>

                                    <button data-mdb-button-init data-mdb-ripple-init
                                            className="btn btn-outline-light btn-lg px-5" type="submit">Войти
                                    </button>
                                </div>

                                <div>
                                    <p className="mb-0"> <a href="#!" className="text-white-50 fw-bold">Зарегестрироваться</a>
                                    </p>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Login;
