import React, { useState, useEffect } from 'react';
import Header from './Header';

function Cart() {
    const [cartItems, setCartItems] = useState([]);

    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/cart', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                if (!response.ok) {
                    throw new Error('Failed to fetch cart items');
                }
                const cartData = await response.json();
                const cartProducts = await Promise.all(cartData.jewerly_id.map(async (productId) => {
                    const productResponse = await fetch(`/ware/${productId}`);
                    if (!productResponse.ok) {
                        throw new Error(`Failed to fetch product with id ${productId}`);
                    }
                    return productResponse.json();
                }));

                const uniqueItems = cartProducts.reduce((acc, item) => {
                    if (!acc[item.id]) {
                        acc[item.id] = { ...item, count: 0 };
                    }
                    acc[item.id].count++;
                    return acc;
                }, {});

                setCartItems(Object.values(uniqueItems));
            } catch (error) {
                console.error('Error fetching cart items:', error);
            }
        };

        fetchCartItems();
    }, []);

    const handleQuantityChange = async (productId, action) => {
        try {
            const token = localStorage.getItem('token');
            const updatedCartItems = cartItems.map(item => {
                if (item.id === productId) {
                    if (action === 'increase') {
                        item.count++;
                    } else if (action === 'decrease' && item.count > 0) {
                        item.count--;
                    }
                }
                return item;
            }).filter(item => item.count > 0);

            setCartItems(updatedCartItems);

            const updatedJewerlyIds = updatedCartItems.flatMap(item => Array(item.count).fill(item.id));
            await fetch('/cart-edit', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ jewerly_id: updatedJewerlyIds })
            });
        } catch (error) {
            console.error('Error updating cart items:', error);
        }
    };

    return (
        <section className="h-100 gradient-custom">
            <div className="container py-5">
                <div className="row d-flex justify-content-center my-4">
                    <div className="col-md-8">
                        <div className="card mb-4">
                            <div className="card-header py-3">
                                <h5 className="mb-0">Cart - {cartItems.length} items</h5>
                            </div>
                            <div className="card-body">
                                {cartItems.map((item, index) => (
                                    <CartItem key={index} item={item} handleQuantityChange={handleQuantityChange} />
                                ))}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

const CartItem = ({ item, handleQuantityChange }) => {
    return (
        <div className="row">
            <div className="col-lg-3 col-md-12 mb-4 mb-lg-0">
                <div className="bg-image hover-overlay hover-zoom ripple rounded" data-mdb-ripple-color="light">
                    <img src={item.image_url} className="w-100" alt="Product" />
                    <a href="#!">
                        <div className="mask" style={{ backgroundColor: "rgba(251, 251, 251, 0.2)" }}></div>
                    </a>
                </div>
            </div>

            <div className="col-lg-5 col-md-6 mb-4 mb-lg-0">
                <p><strong>{item.name}</strong></p>
                <p>Color: {item.stone_name}</p>
            </div>

            <div className="col-lg-4 col-md-6 mb-4 mb-lg-0">
                <div className="d-flex mb-4" style={{ maxWidth: "300px" }}>
                    <img src="/path/to/minus-icon.png" className="btn-icon" alt="Minus" onClick={() => handleQuantityChange(item.id, 'decrease')} />

                    <div className="form-outline">
                        <input id={`quantity-${item.id}`} min="0" name="quantity" value={item.count} type="number" className="form-control" readOnly />
                        <label className="form-label" htmlFor={`quantity-${item.id}`}>Количество</label>
                    </div>

                    <img src="/path/to/plus-icon.png" className="btn-icon" alt="Plus" onClick={() => handleQuantityChange(item.id, 'increase')} />
                </div>

                <p className="text-start text-md-center">
                    <strong>${item.price}</strong>
                </p>
            </div>
        </div>
    );
};

export default Cart;
