import React, { useState } from 'react';
import {Link, useNavigate} from 'react-router-dom';
import './Login.css'; // Подключаем файл стилей для оформления формы

function Register() {
    const [surname, setSurname] = useState('');
    const [name, setName] = useState('');
    const [secondname, setSecondname] = useState('');
    const [number, setNumber] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Проверяем, совпадают ли пароль и подтверждение пароля
        if (password !== confirmPassword) {
            setError('Пароли не совпадают');
            return;
        }

        try {
            const response = await fetch('/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ surname, name, secondname, number, email, password }),
            });

            if (response.ok) {
                const data = await response.json();
                // Если регистрация прошла успешно, переходим на страницу входа
                navigate('/login');
            } else {
                const data = await response.json();
                setError(data.message);
            }
        } catch (error) {
            console.error('Error registering', error);
            setError('Server error');
        }
    };

    return (
        <section className="vh-100 gradient-custom">
            <div className="container py-5 h-100">
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className="col-12 col-md-8 col-lg-6 col-xl-5">
                        <div className="card bg-dark text-white" style={{ borderRadius: "1rem" }}>
                            <div className="card-body p-5 text-center">
                                <div className="mb-md-0 mt-md-0 pb-1">
                                    <h2 className="fw-bold mb-2 text-uppercase">AVE.DIAMONDS</h2>
                                    <p className="fs-5 text-white-60 mb-3">Регистрация</p>
                                    <form onSubmit={handleSubmit}>
                                        {/* Поле фамилии */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="surname" className="fs-5 col-4 col-form-label">Фамилия</label>
                                            <div className="col-8">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    id="surname"
                                                    placeholder="Фамилия"
                                                    value={surname}
                                                    onChange={(e) => {setSurname(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        {/* Поле имени */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="name" className="fs-5 col-4 col-form-label">Имя</label>
                                            <div className="col-8">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    id="name"
                                                    placeholder="Имя"
                                                    value={name}
                                                    onChange={(e) => {setName(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        {/* Поле отчества */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="secondname" className="fs-5 col-4 col-form-label">Отчество</label>
                                            <div className="col-8">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    id="secondname"
                                                    placeholder="Отчество"
                                                    value={secondname}
                                                    onChange={(e) => {setSecondname(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        {/* Поле номера телефона */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="number" className="fs-5 col-4 col-form-label">Номер телефона</label>
                                            <div className="col-8">
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    id="number"
                                                    placeholder="Номер телефона"
                                                    value={number}
                                                    onChange={(e) => {setNumber(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        {/* Поле электронной почты */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="email" className="fs-5 col-4 col-form-label">Почта</label>
                                            <div className="col-8">
                                                <input
                                                    type="email"
                                                    className="form-control"
                                                    id="email"
                                                    placeholder="email@example.com"
                                                    value={email}
                                                    onChange={(e) => {setEmail(e.target.value);}}
                                                />
                                            </div>
                                        </div>
                                        {/* Поле пароля */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="password" className="fs-5 col-4 col-form-label">Пароль</label>
                                            <div className="col-8">
                                                <input
                                                    type="password"
                                                    className="form-control"
                                                    id="password"
                                                    placeholder="********"
                                                    value={password}
                                                    onChange={(e) => { setPassword(e.target.value); }}
                                                />
                                            </div>
                                        </div>
                                        {/* Подтверждение пароля */}
                                        <div className="mb-3 row align-items-center">
                                            <label htmlFor="confirmPassword" className="fs-5 col-4 col-form-label">Подтвердите пароль</label>
                                            <div className="col-8">
                                                <input
                                                    type="password"
                                                    className="form-control"
                                                    id="confirmPassword"
                                                    placeholder="********"
                                                    value={confirmPassword}
                                                    onChange={(e) => { setConfirmPassword(e.target.value); }}
                                                />
                                            </div>
                                        </div>
                                        <p className="small mb-3 pb-lg-2">
                                            <Link to="/login" className="text-white-50 fw-bold">Уже зарегестрированы? Войти</Link>
                                        </p>
                                        <button className="btn btn-outline-light btn-lg px-5" type="submit">Зарегистрироваться</button>
                                    </form>
                                </div>
                                <div>
                                    {error && <p className="text-danger mt-0">{error}</p>}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

export default Register;
