import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Header from './Header';
import { addToCart } from './js/addToCart';
import './All.css';

function Catalog() {
    const navigate = useNavigate();

    const [products, setProducts] = useState([]);
    const [sortByPrice, setSortByPrice] = useState('asc');
    const [filters, setFilters] = useState({
        stone_name: '',
        metal_name: '',
        purity: '',
        carat: '',
        jewerly_type: ''
    });

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const sortProductsByPrice = (products, sortByPrice) => {
        const sortedProducts = [...products];
        sortedProducts.sort((a, b) => {
            if (sortByPrice === 'asc') {
                return a.price - b.price;
            } else {
                return b.price - a.price;
            }
        });
        return sortedProducts;
    };

    const handleSortByPrice = (e) => {
        setSortByPrice(e.target.value);
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters((prevFilters) => ({
            ...prevFilters,
            [name]: value
        }));
    };

    const filterProducts = (products) => {
        return products.filter((product) => {
            return (
                (filters.stone_name === '' || product.stone_name === filters.stone_name) &&
                (filters.metal_name === '' || product.metal_name === filters.metal_name) &&
                (filters.purity === '' || product.purity === filters.purity) &&
                (filters.carat === '' || product.carat === filters.carat) &&
                (filters.jewerly_type === '' || product.jewerly_type === filters.jewerly_type)
            );
        });
    };

    const filteredAndSortedProducts = sortProductsByPrice(filterProducts(products), sortByPrice);

    return (
        <section className="vh-100">
            <div className="container">
                <Header />
                <h1 className="mb-4">Каталог</h1>
                <div className="row mb-3">
                    <div className="col">
                        <label htmlFor="sortByPrice" className="form-label">Сортировать по цене</label>
                        <select
                            id="sortByPrice"
                            className="form-select small-select"
                            value={sortByPrice}
                            onChange={handleSortByPrice}
                        >
                            <option value="asc">По возрастанию цены</option>
                            <option value="desc">По убыванию цены</option>
                        </select>
                    </div>
                </div>
                <div className="row mb-3">
                    <div className="col">
                        <label htmlFor="filterStoneName" className="form-label">Тип камня</label>
                        <select
                            id="filterStoneName"
                            name="stone_name"
                            className="form-select small-select"
                            value={filters.stone_name}
                            onChange={handleFilterChange}
                        >
                            <option value="">Все</option>
                            {/* Добавьте опции для всех типов камней */}
                            <option value="diamond">Алмаз</option>
                            <option value="ruby">Рубин</option>
                            {/* Примерные значения */}
                        </select>
                    </div>
                    <div className="col">
                        <label htmlFor="filterMetalName" className="form-label">Тип металла</label>
                        <select
                            id="filterMetalName"
                            name="metal_name"
                            className="form-select small-select"
                            value={filters.metal_name}
                            onChange={handleFilterChange}
                        >
                            <option value="">Все</option>
                            {/* Добавьте опции для всех типов металлов */}
                            <option value="gold">Золото</option>
                            <option value="silver">Серебро</option>
                            {/* Примерные значения */}
                        </select>
                    </div>
                    <div className="col">
                        <label htmlFor="filterPurity" className="form-label">Чистота</label>
                        <select
                            id="filterPurity"
                            name="purity"
                            className="form-select small-select"
                            value={filters.purity}
                            onChange={handleFilterChange}
                        >
                            <option value="">Все</option>
                            {/* Добавьте опции для всех чистот */}
                            <option value="18k">18 карат</option>
                            <option value="24k">24 карата</option>
                            {/* Примерные значения */}
                        </select>
                    </div>
                    <div className="col">
                        <label htmlFor="filterCarat" className="form-label">Карат</label>
                        <select
                            id="filterCarat"
                            name="carat"
                            className="form-select small-select"
                            value={filters.carat}
                            onChange={handleFilterChange}
                        >
                            <option value="">Все</option>
                            {/* Добавьте опции для всех карат */}
                            <option value="1">1 карат</option>
                            <option value="2">2 карата</option>
                            {/* Примерные значения */}
                        </select>
                    </div>
                    <div className="col">
                        <label htmlFor="filterJewerlyType" className="form-label">Тип ювелирного изделия</label>
                        <select
                            id="filterJewerlyType"
                            name="jewerly_type"
                            className="form-select small-select"
                            value={filters.jewerly_type}
                            onChange={handleFilterChange}
                        >
                            <option value="">Все</option>
                            {/* Добавьте опции для всех типов ювелирных изделий */}
                            <option value="ring">Кольцо</option>
                            <option value="necklace">Ожерелье</option>
                            {/* Примерные значения */}
                        </select>
                    </div>
                </div>
                <div className="row row-cols-1 row-cols-md-3 g-4">
                    {filteredAndSortedProducts.map((product, index) => (
                        <div key={index} className="col-md-2">
                            <div className="card h-100">
                                <div className="card-body">
                                    <Link to={`/product/${product.id}`}>
                                        <img src={product.image_url} className="card-img-top" alt="Product"/>
                                        <h5 className="card-title">{product.name}</h5>
                                        <p className="card-text">{product.stone_name} {product.carat}</p>
                                        <p className="card-text">{product.metal_name} {product.purity}</p>
                                    </Link>
                                    <div className="d-flex justify-content-between">
                                        <p className="card-text">{product.price * (1 + product.percent / 100)} ₽</p>
                                        <button className="btn btn-primary" onClick={() => addToCart(product.id, navigate())}>В корзину</button>
                                    </div>
                                </div>
                            </div >
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Catalog;
