import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function ProductCarousel() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const productsData = await response.json();
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        }
        fetchProducts();
    }, []);

    return (
        <section className="vh-100">
            <div id="multiCarousel" className="carousel slide w-100" data-ride="carousel">
                <div className="carousel-inner w-100" role="listbox">
                    {products.map((product, index) => (
                        <div className={`carousel-item ${index === 0 ? 'active' : ''}`} key={product.id}>
                            <img className="d-block w-100" src={product.image_url} alt={product.name} />
                            <div className="carousel-caption">
                                <h3>{product.name}</h3>
                                <p>{product.stone_name} {product.carat}</p>
                                <p>{product.metal_name} {product.purity}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <a className="carousel-control-prev" href="#multiCarousel" role="button" data-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="sr-only">Previous</span>
                </a>
                <a className="carousel-control-next" href="#multiCarousel" role="button" data-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="sr-only">Next</span>
                </a>
            </div>
        </section>
    );
}

export default ProductCarousel;
