import React, { useState, useEffect } from 'react';
import Header from "./Header";

function Catalog() {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState({}); // Состояние корзины с товарами и их количеством

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const addToCart = (productId) => {
        // Проверяем, есть ли товар уже в корзине
        if (cart[productId]) {
            setCart({ ...cart, [productId]: cart[productId] + 1 }); // Увеличиваем количество товара на 1
        } else {
            setCart({ ...cart, [productId]: 1 }); // Добавляем новый товар в корзину
        }
    };

    const removeFromCart = (productId) => {
        if (cart[productId] > 1) {
            setCart({ ...cart, [productId]: cart[productId] - 1 }); // Уменьшаем количество товара на 1
        } else {
            const updatedCart = { ...cart };
            delete updatedCart[productId]; // Удаляем товар из корзины, если его количество равно 1
            setCart(updatedCart);
        }
    };

    return (
        <div className="container">
            <Header/>
            <h1 className="mb-4">Каталог</h1>
            <div className="row mb-3">
                <div className="col">
                    <label htmlFor="sortByPrice" className="form-label">Фильтр</label>
                    <select
                        id="sortByPrice"
                        className="form-select"
                        value={sortByPrice}
                        onChange={handleSortByPrice}
                    >
                        <option value="asc">По возрастанию цены</option>
                        <option value="desc">По убыванию цены</option>
                    </select>
                </div>
            </div>
            <div className="row row-cols-1 row-cols-md-3 g-4">
                {products.map((product, index) => (
                    <div key={index} className="col-md-4">
                        <div className="card h-100">
                            <img src={product.image_url} className="card-img-top" alt="Product" />
                            <div className="card-body">
                                <h5 className="card-title">{product.name}</h5>
                                <p className="card-text">{product.stone_name} {product.carat}</p>
                                <p className="card-text">{product.metal_name} {product.purity}</p>
                                <div className="d-flex justify-content-between align-items-center">
                                    <p className="card-text">{product.price * (1 + product.percent / 100)}</p>
                                    {cart[product.id] ? (
                                        <div className="btn-group" role="group">
                                            <button className="btn btn-primary" onClick={() => removeFromCart(product.id)}>-</button>
                                            <button className="btn btn-secondary">{cart[product.id]}</button>
                                            <button className="btn btn-primary" onClick={() => addToCart(product.id)}>+</button>
                                        </div>
                                    ) : (
                                        <button className="btn btn-primary" onClick={() => addToCart(product.id)}>В корзину</button>
                                    )}
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Catalog;
