import React, { useState, useEffect } from 'react';
import Header from "./Header";

function Catalog() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    return (
        <div className="container">
            <Header/>
            <h1>Catalog</h1>
            <div className="product-list">
                {products.map((product, index) => (
                    <div key={index} className="product">
                        <h2>{product.name}</h2>
                        <p>Jewelry Type: {product.jewerly_type_name}</p>
                        <p>Stone: {product.stone_name}</p>
                        <p>Carat: {product.carat}</p>
                        <p>Metal: {product.metal_name}</p>
                        <p>Purity: {product.purity}</p>
                        <p>Price: {product.price * (product.percent + 100)}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Catalog;
