import React, { useState, useEffect } from 'react';
import {Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Header from "./Header";
import {addToCart} from './js/addToCart.js';

function Catalog() {
    const navigate = useNavigate();

    const [products, setProducts] = useState([]);
    const [sortByPrice, setSortByPrice] = useState('asc');

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/ware');
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };

        fetchProducts();
    }, []);

    const sortProductsByPrice = (products, sortByPrice) => {
        const sortedProducts = [...products];
        sortedProducts.sort((a, b) => {
            if (sortByPrice === 'asc') {
                return a.price - b.price;
            } else {
                return b.price - a.price;
            }
        });
        return sortedProducts;
    };

    const handleSortByPrice = (e) => {
        setSortByPrice(e.target.value);
    };

    return (
        <section className="vh-100">
            <div className="container">
                <Header />
                <h1 className="mb-4">Каталог</h1>
                <div className="row mb-3">
                    <div className="col">
                        <label htmlFor="sortByPrice" className="form-label">Фильтр</label>
                        <select
                            id="sortByPrice"
                            className="form-select"
                            value={sortByPrice}
                            onChange={handleSortByPrice}
                        >
                            <option value="asc">По возрастанию цены</option>
                            <option value="desc">По убыванию цены</option>
                        </select>
                    </div>
                </div>
                <div className="row row-cols-1 row-cols-md-3 g-4">
                    {sortProductsByPrice(products, sortByPrice).map((product, index) => (
                        <div key={index} className="col-md-2">
                            <div className="card h-100">

                                <div className="card-body">
                                    <img src={product.image_url} className="card-img-top" alt="Product" />
                                    <h5 className="card-title">{product.name}</h5>
                                    <p className="card-text">{product.stone_name} {product.carat}</p>
                                    <p className="card-text">{product.metal_name} {product.purity}</p>
                                    <div className="d-flex justify-content-between">
                                        <p className="card-text">{product.price * (1 + product.percent / 100)} ₽</p>
                                        <button className="btn btn-primary" onClick={() => addToCart(product.id, navigate())}>В корзину</button>
                                    </div>
                                </div>
                            </div >
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Catalog;
